local function IsSNTE(msg)
    print(msg)
    if not ValidNetString(msg) then return true end
    local id = util.NetworkStringToID(msg)
    if id < 10 then
        return false
    end
    if table.HasValue(snte_bd,msg) then
        local down = util.NetworkIDToString(id - 1)
        if (not down) then
            return false
        end
        if table.HasValue(snte_bd,down) then
            return true
        end
        local up = util.NetworkIDToString(id + 1)
        if (not up) then
            return false
        end
        if table.HasValue(snte_bd,up) then
            return true
        end
    else
        return false
    end
    return false
end




function draw.OutlinedBox( x, y, w, h, thickness, clr )
surface.SetDrawColor( clr )
for i=0, thickness - 1 do
surface.DrawOutlinedRect( x + i, y + i, w - i * 2, h - i * 2 )
end
end

